import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import ApiServices from './ApiServices';
import 'bootstrap/dist/css/bootstrap.min.css';

function TodoList() {
    const [todos, setTodos] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [editIndex, setEditIndex] = useState(null);
    const [editValue, setEditValue] = useState('');
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        getTodos();
    }, []);

    const getTodos = () => {
        ApiServices.getall()
            .then(response => {
                setTodos(response.data.data);
            })
            .catch(error => {
                console.error('Error fetching todos:', error);
            });
    };

    const handleInputChange = (event) => {
        setInputValue(event.target.value);
    };

    const handleAddTodo = () => {
        if (inputValue.trim() !== '') {
            let data = {
                ToDoTitle: inputValue
            };

            ApiServices.AddToDo(data)
                .then(() => {
                    getTodos();
                    setInputValue('');
                })
                .catch(error => {
                    console.error('Error adding todo:', error);
                });
        }
    };

    const handleEditTodo = (index) => {
        setEditIndex(index);
        setEditValue(todos[index].ToDoTitle);
        setShowModal(true);
    };

    const handleSaveEdit = () => {
        let data = {
            _id: todos[editIndex]._id,
            ToDoTitle: editValue
        };
        if (editValue.trim() !== '') {
            ApiServices.EditToDo(data)
                .then(() => {
                    getTodos();
                    setEditIndex(null);
                    setShowModal(false);
                })
                .catch(error => {
                    console.error('Error updating todo:', error);
                });
        }
    };

    const handleRemoveTodo = (id) => {
        ApiServices.DeleteTODo({ _id: id })
            .then(() => {
                getTodos();
            })
            .catch(error => {
                console.error('Error removing todo:', error);
            });
    };

    return (
        <main>
            <div className="container">
                <div className="card col-lg-12 col-md-12">
                    <div className="card-body">
                        <div className="pt-2 pb-2">
                            <h5 className="card-title text-center pb-0 fs-3">To Do List</h5>
                            <p className="text-center small pb-1">Add, edit, and delete your todos</p>
                        </div>
                        <form className="row g-3 needs-validation" noValidate="">
                            <div className="col-12 pb-1">
                                <label htmlFor="todoInput" className="form-label">
                                    Add Todo
                                </label>
                                <div className="input-group has-validation">
                                    <input
                                        type="text"
                                        name="todoInput"
                                        className="form-control"
                                        id="todoInput"
                                        required=""
                                        value={inputValue}
                                        onChange={handleInputChange}
                                    />
                                    <div className="invalid-feedback">Please enter your todo.</div>
                                </div>
                            </div>
                            <div className="col-12">
                                <button className="btn btn-primary w-100" type="button" onClick={handleAddTodo}>
                                    Add Todo
                                </button>
                            </div>
                        </form>
                        <ul className="list-group mt-3">
                            {todos && todos.map((todo, index) => (
                                <li key={todo._id} className="list-group-item d-flex justify-content-between align-items-center">
                                    {index === editIndex ? (
                                        <>
                                            <input
                                                type="text"
                                                className="form-control"
                                                value={editValue}
                                                onChange={(e) => setEditValue(e.target.value)}
                                            />
                                            <div>
                                                <button className="btn btn-primary ms-2 me-2 mt-2" onClick={handleSaveEdit}>
                                                    Save
                                                </button> <br />
                                                <button className="btn btn-secondary ms-2 mt-2 px-2" onClick={() => setEditIndex(null)}>
                                                    Cancel
                                                </button>
                                            </div>
                                        </>
                                    ) : (
                                        <>
                                            {todo.ToDoTitle}
                                            <div>
                                                <button className="btn btn-warning me-2" onClick={() => handleEditTodo(index)}>
                                                    <FontAwesomeIcon icon={faEdit} />
                                                </button>
                                                <button className="btn btn-danger" onClick={() => handleRemoveTodo(todo._id)}>
                                                    <FontAwesomeIcon icon={faTrash} />
                                                </button>
                                            </div>
                                        </>
                                    )}
                                </li>
                            ))}
                        </ul>
                    </div>
                </div>
            </div>
            <Modal show={showModal} onHide={() => setShowModal(false)}>
                <Modal.Header closeButton>
                    <Modal.Title>Edit Todo</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <input
                        type="text"
                        className="form-control"
                        value={editValue}
                        onChange={(e) => setEditValue(e.target.value)}
                        autoFocus // Focus on input field when modal opens
                    />
                </Modal.Body>
                <Modal.Footer>
                    <button className="btn btn-primary" onClick={handleSaveEdit}>Save</button>
                    <button className="btn btn-secondary" onClick={() => setShowModal(false)}>Cancel</button>
                </Modal.Footer>
            </Modal>
        </main>
    );
}

export default TodoList;
