import React, { useState } from 'react';
import { PacmanLoader } from 'react-spinners';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [load, setLoad] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]);
      setInputValue('');
    }
  };

  const handleRemoveTodo = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const handleEditTodo = (index) => {
    setEditIndex(index);
    setEditValue(todos[index]);
    setShowEditModal(true); // Show the edit modal
  };

  const handleSaveEdit = () => {
    if (editValue.trim() !== '') {
      const newTodos = [...todos];
      newTodos[editIndex] = editValue;
      setTodos(newTodos);
      setEditIndex(null);
      setShowEditModal(false);
    }
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    setShowEditModal(false);
  };

  return (
    <>
      <main>
        <div className="container">
          <section className="section register min-vh-100 d-flex flex-column align-items-center justify-content-center py-4">
            <div className="container">
              <div className="row justify-content-center">
                <div className="col-lg-4 col-md-6 d-flex flex-column align-items-center justify-content-center">
                  <div className="d-flex justify-content-center py-4">
                    <div className="logo d-flex align-items-center w-auto">
                      <span className="d-none d-lg-block">To Do App</span>
                    </div>
                  </div>

                  <PacmanLoader color="blue" size={40} cssOverride={{ display: 'block', margin: '0 auto' }} loading={load} />
                  <section className={load === true ? 'd-none' : 'my-5'}>
                    <div className="card mb-3">
                      <div className="card-body">
                        <div className="pt-4 pb-2">
                          <h5 className="card-title text-center pb-0 fs-4">To Do List</h5>
                          <p className="text-center small">Add, edit, and delete your todos</p>
                        </div>
                        <form className="row g-3 needs-validation" noValidate="">
                          <div className="col-12">
                            <label htmlFor="todoInput" className="form-label">
                              Add Todo
                            </label>
                            <div className="input-group has-validation">
                              <input
                                type="text"
                                name="todoInput"
                                className="form-control"
                                id="todoInput"
                                required=""
                                value={inputValue}
                                onChange={handleInputChange}
                              />
                              <div className="invalid-feedback">Please enter your todo.</div>
                            </div>
                          </div>
                          <div className="col-12">
                            <button className="btn btn-primary w-100" type="button" onClick={handleAddTodo}>
                              Add Todo
                            </button>
                          </div>
                        </form>
                        <ul className="list-group mt-3">
                          {todos.map((todo, index) => (
                            <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
                              {index === editIndex ? (
                                <>
                                  <input
                                    type="text"
                                    className="form-control"
                                    value={editValue}
                                    onChange={(e) => setEditValue(e.target.value)}
                                  />
                                  <div>
                                    <button className="btn btn-primary me-2" onClick={handleSaveEdit}>
                                      Save
                                    </button>
                                    <button className="btn btn-secondary" onClick={handleCancelEdit}>
                                      Cancel
                                    </button>
                                  </div>
                                </>
                              ) : (
                                <>
                                  {todo}
                                  <div>
                                    <button className="btn btn-warning me-2" onClick={() => setShowEditModal(true)}>
                                      <FontAwesomeIcon icon={faEdit} />
                                    </button>
                                    <button className="btn btn-danger" onClick={() => handleRemoveTodo(index)}>
                                      <FontAwesomeIcon icon={faTrash} />
                                    </button>
                                  </div>
                                </>
                              )}
                            </li>
                          ))}
                        </ul>
                      </div>
                    </div>
                  </section>
                </div>
              </div>
            </div>
          </section>
        </div>
      </main>

      {showEditModal && (
        <div className="modal" tabIndex="-1" role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Todo</h5>
                <button type="button" className="close" onClick={handleCancelEdit} aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <input
                  type="text"
                  className="form-control"
                  value={editValue}
                  onChange={(e) => setEditValue(e.target.value)}
                />
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary" onClick={handleSaveEdit}>
                  Save
                </button>
                <button type="button" className="btn btn-secondary" onClick={handleCancelEdit}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default TodoList;
