import React, { useState } from 'react';
import { toast } from 'react-toastify';
import axios from 'axios';
import 'react-toastify/dist/ReactToastify.css';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editedTodo, setEditedTodo] = useState(null);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleAddTodo = async () => {
    try {
      const response = await axios.post('http://localhost:4001/api/todos/add', {
        title: inputValue,
        description: ''
      });
      if (response.data.success) {
        setTodos([...todos, response.data.todo]);
        setInputValue('');
        toast.success('Todo added successfully');
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      console.error('Error adding todo:', error);
      toast.error('Error adding todo. Please try again later.');
    }
  };

  const handleRemoveTodo = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:4001/api/todos/${id}`);
      if (response.data.success) {
        setTodos(todos.filter(todo => todo.id !== id));
        toast.success('Todo deleted successfully');
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      console.error('Error deleting todo:', error);
      toast.error('Error deleting todo. Please try again later.');
    }
  };

  const handleEditTodo = (todo) => {
    setEditedTodo(todo);
  };

  const handleSaveEdit = async () => {
    try {
      const response = await axios.put(`http://localhost:4001/api/todos/${editedTodo.id}`, editedTodo);
      if (response.data.success) {
        setTodos(todos.map(todo => (todo.id === editedTodo.id ? editedTodo : todo)));
        setEditedTodo(null);
        toast.success('Todo updated successfully');
      } else {
        toast.error(response.data.message);
      }
    } catch (error) {
      console.error('Error updating todo:', error);
      toast.error('Error updating todo. Please try again later.');
    }
  };

  const handleCancelEdit = () => {
    setEditedTodo(null);
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <div className="card">
            <div className="card-header">Todo List</div>
            <div className="card-body">
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Add Todo"
                  value={inputValue}
                  onChange={handleInputChange}
                />
              </div>
              <button className="btn btn-primary mb-3" onClick={handleAddTodo}>
                Add Todo
              </button>
              <ul className="list-group">
                {todos.map(todo => (
                  <li key={todo.id} className="list-group-item d-flex justify-content-between align-items-center">
                    {editedTodo && editedTodo.id === todo.id ? (
                      <input
                        type="text"
                        className="form-control"
                        value={editedTodo.title}
                        onChange={e => setEditedTodo({ ...editedTodo, title: e.target.value })}
                      />
                    ) : (
                      <span>{todo.title}</span>
                    )}
                    <div>
                      {editedTodo && editedTodo.id === todo.id ? (
                        <>
                          <button className="btn btn-primary me-2" onClick={handleSaveEdit}>
                            Save
                          </button>
                          <button className="btn btn-secondary" onClick={handleCancelEdit}>
                            Cancel
                          </button>
                        </>
                      ) : (
                        <>
                          <button className="btn btn-warning me-2" onClick={() => handleEditTodo(todo)}>
                            Edit
                          </button>
                          <button className="btn btn-danger" onClick={() => handleRemoveTodo(todo.id)}>
                            Delete
                          </button>
                        </>
                      )}
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
      {editedTodo && (
        <div className="modal-box">
          <div className="modal-content">
            <span className="close" onClick={handleCancelEdit}>&times;</span>
            <h2>Edit Todo</h2>
            <input
              type="text"
              className="form-control"
              value={editedTodo.title}
              onChange={e => setEditedTodo({ ...editedTodo, title: e.target.value })}
            />
            <button className="btn btn-primary mt-2" onClick={handleSaveEdit}>
              Save
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default TodoList;
