import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    // Perform login logic here
  };

  return (
    <Container className="d-flex flex-column align-items-center justify-content-center" style={{ minHeight: '100vh' }}>
      <figure aria-hidden="true" className="person mb-4">
        <div className="person-body"></div>
        <div className="neck skin"></div>
        <div className="head skin">
          <div className={`eyes ${showPassword ? 'closed' : 'open'}`}></div>
          <div className="mouth"></div>
        </div>
        <div className="hair"></div>
        <div className="ears"></div>
        <div className="shirt-1"></div>
        <div className="shirt-2"></div>
      </figure>

      <Form onSubmit={handleLogin} className="bg-white p-4 rounded shadow-sm" style={{ maxWidth: '400px', width: '100%' }}>
        <h1 className="text-center mb-4">Login Form</h1>
        <Form.Group controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control 
            type="email" 
            placeholder="Enter email" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
            required 
          />
        </Form.Group>

        <Form.Group controlId="formBasicPassword">
          <Form.Label>Password</Form.Label>
          <Form.Control 
            type={showPassword ? "text" : "password"} 
            placeholder="Password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
            required 
          />
        </Form.Group>

        <Form.Group controlId="formBasicCheckbox" className="mb-3">
          <Form.Check 
            type="checkbox" 
            label="Show Password" 
            onChange={() => setShowPassword(!showPassword)} 
          />
        </Form.Group>

        <Button variant="primary" type="submit" className="w-100">
          Log In
        </Button>

        <div className="text-center mt-3">
          <a href="/register">Register</a>
        </div>
      </Form>
    </Container>
  );
}

export default Login;
