import React, { useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

const TodoList = () => {
  const [todos, setTodos] = useState([]);
  const [editingIndex, setEditingIndex] = useState(null);
  const [newTodo, setNewTodo] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    fetchTodos();
  }, []);

  const fetchTodos = () => {
    // Fetch todos from your API or localStorage
    // For demonstration, I'm initializing todos with dummy data
    const dummyTodos = [
      { id: 1, text: 'Example Todo 1' },
      { id: 2, text: 'Example Todo 2' },
      { id: 3, text: 'Example Todo 3' }
    ];
    setTodos(dummyTodos);
  };

  const handleEditTodo = (index) => {
    setEditingIndex(index);
    setNewTodo(todos[index].text);
    setShowEditModal(true);
  };

  const handleSaveEdit = () => {
    if (newTodo.trim() !== '') {
      const updatedTodos = [...todos];
      updatedTodos[editingIndex].text = newTodo;
      setTodos(updatedTodos);
      setEditingIndex(null);
      setShowEditModal(false);
    }
  };

  const handleCancelEdit = () => {
    setEditingIndex(null);
    setShowEditModal(false);
  };

  const handleDeleteTodo = (index) => {
    const updatedTodos = todos.filter((todo, i) => i !== index);
    setTodos(updatedTodos);
  };

  const handleNewTodoChange = (event) => {
    setNewTodo(event.target.value);
  };

  return (
    <div>
      <h1>Todo List</h1>
      <ul>
        {todos.map((todo, index) => (
          <li key={todo.id}>
            {editingIndex === index ? (
              <div>
                <input
                  type="text"
                  value={newTodo}
                  onChange={handleNewTodoChange}
                />
                <button onClick={handleSaveEdit}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            ) : (
              <div>
                {todo.text}
                <button onClick={() => handleEditTodo(index)}>
                  <FontAwesomeIcon icon={faEdit} />
                </button>
                <button onClick={() => handleDeleteTodo(index)}>
                  <FontAwesomeIcon icon={faTrash} />
                </button>
              </div>
            )}
          </li>
        ))}
      </ul>
      {showEditModal && (
        <div className="modal">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Todo</h5>
                <button
                  type="button"
                  className="close"
                  onClick={handleCancelEdit}
                >
                  <span>&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <input
                  type="text"
                  value={newTodo}
                  onChange={handleNewTodoChange}
                />
              </div>
              <div className="modal-footer">
                <button onClick={handleSaveEdit}>Save</button>
                <button onClick={handleCancelEdit}>Cancel</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TodoList;
