import React, { useState } from 'react';

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleAddTodo = () => {
    if (inputValue.trim() !== '') {
      setTodos([...todos, inputValue]);
      setInputValue('');
    }
  };

  const handleRemoveTodo = (index) => {
    const newTodos = [...todos];
    newTodos.splice(index, 1);
    setTodos(newTodos);
  };

  const handleEditTodo = (index) => {
    setEditIndex(index);
    setEditValue(todos[index]);
    setShowEditModal(true);
  };

  const handleSaveEdit = () => {
    if (editValue.trim() !== '') {
      const newTodos = [...todos];
      newTodos[editIndex] = editValue;
      setTodos(newTodos);
      setEditIndex(null);
      setShowEditModal(false);
    }
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    setShowEditModal(false);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Todo List</h2>
      <input
        type="text"
        className="form-control mb-3"
        value={inputValue}
        onChange={handleInputChange}
        placeholder="Enter a new todo"
      />
      <button className="btn btn-primary mb-3" onClick={handleAddTodo}>Add Todo</button>
      <ul className="list-group">
        {todos.map((todo, index) => (
          <li key={index} className="list-group-item d-flex justify-content-between align-items-center">
            {todo}
            <div>
              <button className="btn btn-warning mr-2" onClick={() => handleEditTodo(index)}>Edit</button>
              <button className="btn btn-danger" onClick={() => handleRemoveTodo(index)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
      {showEditModal && (
        <div className="modal" tabIndex="-1" role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Edit Todo</h5>
                <button type="button" className="close" onClick={handleCancelEdit} aria-label="Close">
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <input
                  type="text"
                  className="form-control"
                  value={editValue}
                  onChange={(e) => setEditValue(e.target.value)}
                />
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary" onClick={handleSaveEdit}>Save</button>
                <button type="button" className="btn btn-secondary" onClick={handleCancelEdit}>Cancel</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default TodoList;
