import axios from "axios";
export const BASE_URL="http://localhost:4002/"
class ApiServices{
    getToken(){
        let obj={
            Authorization:localStorage.getItem("token")
        }
        return obj
    }
    getLogin(data){
        return axios.post(BASE_URL+"api/user/login",data)
    }
    
    
}
export default new ApiServices
