import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrash, faPlus } from "@fortawesome/free-solid-svg-icons";
import ApiServices from "./ApiServices";
import "bootstrap/dist/css/bootstrap.min.css";

function TodoList() {
  const [todos, setTodos] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [editIndex, setEditIndex] = useState(null);
  const [editValue, setEditValue] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    getTodos();
  }, []);

  const getTodos = () => {
    ApiServices.getall()
      .then((response) => {
        setTodos(response.data.data || []);
      })
      .catch((error) => {
        console.error("Error fetching todos:", error);
      });
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleAddTodo = () => {
    if (inputValue.trim() === "") return;

    const data = {
      ToDoTitle: inputValue.trim(),
      isCompleted: false,
    };

    ApiServices.AddToDo(data)
      .then(() => {
        getTodos();
        setInputValue("");
      })
      .catch((error) => {
        console.error("Error adding todo:", error);
      });
  };

  // Allow Enter key to add todo
  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      handleAddTodo();
    }
  };

  const openModal = () => setIsModalOpen(true);
  const closeModal = () => setIsModalOpen(false);

  const handleEditTodo = (index) => {
    setEditIndex(index);
    setEditValue(todos[index].ToDoTitle);
    openModal();
  };

  const handleSaveEdit = () => {
    if (editValue.trim() === "") return;

    const data = {
      _id: todos[editIndex]._id,
      ToDoTitle: editValue.trim(),
      isCompleted: todos[editIndex].isCompleted,
    };

    ApiServices.EditToDo(data)
      .then(() => {
        getTodos();
        setEditIndex(null);
        closeModal();
      })
      .catch((error) => {
        console.error("Error updating todo:", error);
      });
  };

  const handleCancelEdit = () => {
    setEditIndex(null);
    closeModal();
  };

  const handleRemoveTodo = (id) => {
    ApiServices.DeleteTODo({ _id: id })
      .then(() => {
        getTodos();
      })
      .catch((error) => {
        console.error("Error removing todo:", error);
      });
  };

  return (
    <main className="bg-light min-vh-100 d-flex align-items-center py-4">
      <div className="container">
        <section className="d-flex flex-column align-items-center justify-content-center">
          <div className="row justify-content-center w-100">
            <div className="col-lg-6 col-md-8">
              {/* Header */}
              <div className="text-center mb-4">
                <h1 className="fw-bold mb-1">To Do App</h1>
                <p className="text-muted mb-0">
                  Stay organized by tracking your daily tasks.
                </p>
              </div>

              {/* Card */}
              <div className="card shadow-sm border-0 rounded-3">
                <div className="card-body">
                  {/* Title */}
                  <div className="mb-3 text-center">
                    <h5 className="card-title fw-semibold mb-1">
                      Your To Do List
                    </h5>
                    <p className="text-muted small mb-0">
                      Add, edit, and delete your tasks easily.
                    </p>
                  </div>

                  {/* Add Todo */}
                  <form className="mb-3" onSubmit={(e) => e.preventDefault()}>
                    <label
                      htmlFor="todoInput"
                      className="form-label fw-medium mb-2"
                    >
                      Add a new task
                    </label>
                    <div className="input-group">
                      <input
                        type="text"
                        name="todoInput"
                        className="form-control"
                        id="todoInput"
                        placeholder="e.g. Complete project report"
                        value={inputValue}
                        onChange={handleInputChange}
                        onKeyDown={handleKeyDown}
                      />
                      <button
                        className="btn btn-primary d-flex align-items-center gap-2"
                        type="button"
                        onClick={handleAddTodo}
                      >
                        <FontAwesomeIcon icon={faPlus} />
                        <span>Add</span>
                      </button>
                    </div>
                    <small className="text-muted d-block mt-1">
                      Press <strong>Enter</strong> or click <strong>Add</strong>{" "}
                      to save the task.
                    </small>
                  </form>

                  {/* Todo List */}
                  {todos.length === 0 ? (
                    <div className="text-center py-4 text-muted">
                      <p className="mb-1">No tasks yet.</p>
                      <p className="small mb-0">
                        Start by adding your first todo above.
                      </p>
                    </div>
                  ) : (
                    <ul
                      className="list-group"
                      style={{ maxHeight: "320px", overflowY: "auto" }}
                    >
                      {todos.map((todo, index) => (
                        <li
                          key={todo._id}
                          className="list-group-item d-flex justify-content-between align-items-center border-0 border-bottom"
                        >
                          <div className="d-flex flex-column">
                            <span className="fw-medium">
                              {todo.ToDoTitle}
                            </span>
                          </div>
                          <div className="btn-group" role="group">
                            <button
                              className="btn btn-outline-secondary btn-sm"
                              onClick={() => handleEditTodo(index)}
                              title="Edit task"
                            >
                              <FontAwesomeIcon icon={faEdit} />
                            </button>
                            <button
                              className="btn btn-outline-danger btn-sm"
                              onClick={() => handleRemoveTodo(todo._id)}
                              title="Delete task"
                            >
                              <FontAwesomeIcon icon={faTrash} />
                            </button>
                          </div>
                        </li>
                      ))}
                    </ul>
                  )}
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>

      {/* Edit Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        style={{
          content: {
            top: "50%",
            left: "50%",
            right: "auto",
            bottom: "auto",
            transform: "translate(-50%, -50%)",
            width: "480px",
            maxWidth: "90vw",
            padding: "0",
            borderRadius: "10px",
            border: "none",
            boxShadow: "0 8px 20px rgba(0,0,0,0.15)",
          },
          overlay: {
            backgroundColor: "rgba(0, 0, 0, 0.45)",
          },
        }}
      >
        <div className="modal-content p-4 border-0">
          {/* Header */}
          <div className="d-flex justify-content-between align-items-center mb-3">
            <h4 className="m-0 fw-semibold">Edit Todo</h4>
            <button
              type="button"
              className="btn-close"
              onClick={closeModal}
            ></button>
          </div>

          {/* Body */}
          <div className="mb-4">
            <label className="form-label fw-medium mb-2">Todo Title</label>
            <input
              type="text"
              className="form-control form-control-lg"
              value={editValue}
              onChange={(e) => setEditValue(e.target.value)}
              placeholder="Update your task title"
            />
          </div>

          {/* Footer */}
          <div className="d-flex justify-content-end gap-2">
            <button
              type="button"
              className="btn btn-light border px-4 py-2"
              onClick={handleCancelEdit}
            >
              Cancel
            </button>
            <button
              type="button"
              className="btn btn-primary px-4 py-2 fw-semibold"
              onClick={handleSaveEdit}
            >
              Save Changes
            </button>
          </div>
        </div>
      </Modal>
    </main>
  );
}

export default TodoList;
